import React from 'react';
import './App.css';
import { faPowerOff, faVolumeUp, faVolumeMute } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const firstSoundGroup = [
    {
      keyCode: 81,
      key: 'Q',
      id: 'Heater-1',
      url: 'https://s3.amazonaws.com/freecodecamp/drums/Heater-1.mp3'
    },
    {
      keyCode: 87,
      key: 'W',
      id: 'Heater-2',
      url: 'https://s3.amazonaws.com/freecodecamp/drums/Heater-2.mp3'
    },
    {
      keyCode: 69,
      key: 'E',
      id: 'Heater-3',
      url: 'https://s3.amazonaws.com/freecodecamp/drums/Heater-3.mp3'
    },
    {
      keyCode: 65,
      key: 'A',
      id: 'Heater-4',
      url: 'https://s3.amazonaws.com/freecodecamp/drums/Heater-4_1.mp3'
    },
    {
      keyCode: 83,
      key: 'S',
      id: 'Clap',
      url: 'https://s3.amazonaws.com/freecodecamp/drums/Heater-6.mp3'
    },
    {
      keyCode: 68,
      key: 'D',
      id: 'Open-HH',
      url: 'https://s3.amazonaws.com/freecodecamp/drums/Dsc_Oh.mp3'
    },
    {
      keyCode: 90,
      key: 'Z',
      id: "Kick-n'-Hat",
      url: 'https://s3.amazonaws.com/freecodecamp/drums/Kick_n_Hat.mp3'
    },
    {
      keyCode: 88,
      key: 'X',
      id: 'Kick',
      url: 'https://s3.amazonaws.com/freecodecamp/drums/RP4_KICK_1.mp3'
    },
    {
      keyCode: 67,
      key: 'C',
      id: 'Closed-HH',
      url: 'https://s3.amazonaws.com/freecodecamp/drums/Cev_H2.mp3'
    }
  ];

const secondSoundGroup = [
    {
      keyCode: 81,
      key: 'Q',
      id: 'Chord-1',
      url: 'https://s3.amazonaws.com/freecodecamp/drums/Chord_1.mp3'
    },
    {
      keyCode: 87,
      key: 'W',
      id: 'Chord-2',
      url: 'https://s3.amazonaws.com/freecodecamp/drums/Chord_2.mp3'
    },
    {
      keyCode: 69,
      key: 'E',
      id: 'Chord-3',
      url: 'https://s3.amazonaws.com/freecodecamp/drums/Chord_3.mp3'
    },
    {
      keyCode: 65,
      key: 'A',
      id: 'Shaker',
      url: 'https://s3.amazonaws.com/freecodecamp/drums/Give_us_a_light.mp3'
    },
    {
      keyCode: 83,
      key: 'S',
      id: 'Open-HH',
      url: 'https://s3.amazonaws.com/freecodecamp/drums/Dry_Ohh.mp3'
    },
    {
      keyCode: 68,
      key: 'D',
      id: 'Closed-HH',
      url: 'https://s3.amazonaws.com/freecodecamp/drums/Bld_H1.mp3'
    },
    {
      keyCode: 90,
      key: 'Z',
      id: 'Punchy-Kick',
      url: 'https://s3.amazonaws.com/freecodecamp/drums/punchy_kick_1.mp3'
    },
    {
      keyCode: 88,
      key: 'X',
      id: 'Side-Stick',
      url: 'https://s3.amazonaws.com/freecodecamp/drums/side_stick_1.mp3'
    },
    {
      keyCode: 67,
      key: 'C',
      id: 'Snare',
      url: 'https://s3.amazonaws.com/freecodecamp/drums/Brk_Snr.mp3'
    }
  ];

const soundsName = {
  heaterKit: "Heater Kit",
  smoothPianoKit: "Smooth Piano Kit"
};

const soundsGroup = {
  heaterKit: firstSoundGroup,
  smoothPianoKit: secondSoundGroup
}

const KeyboardKey = ({
  play,
  deactivateAudio,
  sound: { id, key, url, keyCode },
  power
}) => {
  const handleKeyDown = (e) => {
    if (power && keyCode === e.keyCode) {
      const audio = document.getElementById(key);
      play(key, id);
      deactivateAudio(audio);
      const drumPad = document.getElementById(keyCode);
      drumPad.classList.add("active");
      setTimeout(() => {
        drumPad.classList.remove("active");
      }, 200);
    }
  };

  React.useEffect(() => {
    document.addEventListener("keydown", handleKeyDown);
    return () => {
      document.removeEventListener("keydown", handleKeyDown);
    }; // eslint-disable-next-line
  }, [power]);

  const handleClickEvent = () => {
    if (power) {
      play(key, id);
      const drumPad = document.getElementById(keyCode);
      drumPad.classList.add("active");
      setTimeout(() => {
        drumPad.classList.remove("active");
      }, 200);
    }
  };

  return (
    <button
      id={keyCode}
      className="drum-pad"
      onClick={handleClickEvent}
      disabled={!power}
    >
      <audio className="clip" src={url} id={key} />
      {key}
    </button>
  );
};


const Keyboard = ({ sounds, play, power, deactivateAudio }) => (
  <div className="keyboard">
    {power
      ? sounds.map((sound) => (
          <KeyboardKey
            sound={sound}
            play={play}

            power={power}
            key={sound.id}
          />
        ))
      : sounds.map((sound) => (
          <KeyboardKey
            sound={{ ...sound, url: "#" }}
            play={play}

            power={power}
            key={sound.id}
          />
        ))}
  </div>
);


const DrumControl = ({
  handlePowerToggle,
  name,
  soundbankname,
  power,
  volume,
  handleVolumeChange,
  changeSoundGroup
}) => (
  <div className={`controle ${power ? 'power-on' : 'power-off'}`}>
    <h2 id="display">{power ? name : ''}</h2>
    <p id="bank">{power ? `SoundBank: ${soundbankname}` : ''}</p>
    <div className="volume-control">
      <FontAwesomeIcon
        icon={faVolumeMute}
        className={`volume-icon ${volume === 0 ? 'muted' : ''}`}
      />
      <input
        max="1"
        min="0"
        step="0.01"
        type="range"
        value={volume}
        onChange={power ? handleVolumeChange : null}
        className={`volume-bar ${power ? '' : 'power-off'}`}
        disabled={!power}
      />
      <FontAwesomeIcon
        icon={faVolumeUp}
        className={`volume-icon ${volume === 0 ? 'hidden' : ''}`}
      />
    </div>
    <button onClick={changeSoundGroup} className="soundbank">
      Change Sound Bank
    </button>
    <button onClick={handlePowerToggle} className={`power-button ${power ? 'glow' : ''}`}>
      <FontAwesomeIcon icon={faPowerOff} />
    </button>
  </div>
);

const App = () => {
  const [power, setPower] = React.useState(false);
  const [volume, setVolume] = React.useState(1);
  const [soundName, setSoundName] = React.useState("");
  const [soundType, setSoundType] = React.useState("heaterKit");
  const [sounds, setSounds] = React.useState(soundsGroup[soundType]);





  const play = (key, sound) => {
    setSoundName(sound)
    const audio = document.getElementById(key);

    audio.currentTime = 0;
    audio.play();

  }

  const stop = () => {
     setPower(!power)
  }

  const changeSoundGroup = () => {
    setSoundName("")
    if(soundType === "heaterKit"){
        setSoundType("smoothPianoKit");
        setSounds(soundsGroup.smoothPianoKit);
    } else {
        setSoundType("heaterKit");
        setSounds(soundsGroup.heaterKit);
    }
  }

  const handleVolumeChange = e => {
    setVolume(e.target.value)
  }

  const updateKeyVolume = () => {
    const audioes = sounds.map(sound => document.getElementById(sound.key));
    audioes.forEach(audio => {
      if(audio) {
        audio.volume = volume;
      }
    }) 
  }

  return (
    <div id="drum-machine">
      {updateKeyVolume()}
      <div className="wrapper">

        <Keyboard sounds={sounds} play={play} power={power}/>
        <DrumControl 
          handlePowerToggle={stop}
          power={power}
          volume={volume} 
          name = {soundName || soundsName[soundType]}
          changeSoundGroup={changeSoundGroup}
          handleVolumeChange={handleVolumeChange} 
         />
      </div>
    </div>
  )
};


export default App;
