import React from 'react';
import './App.css';
import { faPowerOff, faVolumeUp, faVolumeMute } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { soundsGroup, soundsName } from './Sounds.js';


const KeyboardKey = ({
  play,
  sound: { id, key, url, keyCode },
  power
}) => {


  React.useEffect(() => {
    document.addEventListener("keydown", handleClickEvent);
    return () => {
      document.removeEventListener("keydown", handleClickEvent);
    }; // eslint-disable-next-line
  }, [power]);

  const handleClickEvent = (e) => {
    if (power) {
      const sound = this.state.soundBank.find((sound) => sound.keyCode === e.keyCode);
      if (sound) {
        const audio = new Audio(sound.url);
        audio.volume = this.state.volume / 100;
        audio.play();
      }
    }
  };

  return (
    <button
      id={keyCode}
      className="drum-pad"
      onClick={handleClickEvent}
      disabled={!power}
    >
      <audio className="clip" src={url} id={key} />
      {key}
    </button>
  );
};


const Keyboard = ({ sounds, play, power }) => (
  <div className="keyboard">
    {power
      ? sounds.map((sound) => (
          <KeyboardKey
            sound={sound}
            play={play}
            power={power}
            key={sound.id}
          />
        ))
      : sounds.map((sound) => (
          <KeyboardKey
            sound={{ ...sound, url: "#" }}
            play={play}
            power={power}
            key={sound.id}
          />
        ))}
  </div>
);


const DrumControl = ({
  handlePowerToggle,
  name,
  soundbankname,
  power,
  volume,
  handleVolumeChange,
  changeSoundGroup
}) => {
  const volumeIconClass = volume === 0 ? 'muted' : '';
  const volumeBarClass = power ? '' : 'power-off';
  const powerButtonClass = power ? 'glow' : '';

  return (
    <div className={`controle ${power ? 'power-on' : 'power-off'}`}>
      <h2 id="display">{power ? name : ''}</h2>
      <p id="bank">{power ? `SoundBank: ${soundbankname}` : ''}</p>
      <div className="volume-control">
        <FontAwesomeIcon icon={faVolumeMute} className={`volume-icon ${volumeIconClass}`} />
        <input
          max="1"
          min="0"
          step="0.01"
          type="range"
          value={volume}
          onChange={power ? handleVolumeChange : null}
          className={`volume-bar ${volumeBarClass}`}
          disabled={!power}
        />
        <FontAwesomeIcon icon={faVolumeUp} className={`volume-icon ${volume === 0 ? 'hidden' : ''}`} />
      </div>
      <button onClick={changeSoundGroup} className="soundbank">
        Change Sound Bank
      </button>
      <button onClick={handlePowerToggle} className={`power-button ${powerButtonClass}`}>
        <FontAwesomeIcon icon={faPowerOff} />
      </button>
    </div>
  );
};

const App = () => {
  const [power, setPower] = React.useState(false);
  const [volume, setVolume] = React.useState(1);
  const [soundName, setSoundName] = React.useState("");
  const [soundType, setSoundType] = React.useState("heaterKit");
  const [sounds, setSounds] = React.useState(soundsGroup[soundType]);





  const play = (key, sound) => {
    setSoundName(sound)
    const audio = document.getElementById(key);

    audio.currentTime = 0;
    audio.play();

  }

  const stop = () => {
     setPower(!power)
  }

  const changeSoundGroup = () => {
    setSoundName("");
    setSounds((prevSounds) => {
      if (soundType === "heaterKit") {
        setSoundType("smoothPianoKit");
        return soundsGroup.smoothPianoKit;
      } else {
        setSoundType("heaterKit");
        return soundsGroup.heaterKit;
      }
    });
  };

  const handleVolumeChange = e => {
    setVolume(e.target.value)
  }

  const updateKeyVolume = () => {
    const audioes = sounds.map(sound => document.getElementById(sound.key));
    audioes.forEach(audio => {
      if(audio) {
        audio.volume = volume;
      }
    }) 
  }

  return (
    <div id="drum-machine">
      {updateKeyVolume()}
      <div className="wrapper">

        <Keyboard sounds={sounds} play={play} power={power}/>
        <DrumControl 
          handlePowerToggle={stop}
          power={power}
          volume={volume} 
          name = {soundName || soundsName[soundType]}
          soundbankname={soundsName[soundType]}
          changeSoundGroup={changeSoundGroup}
          handleVolumeChange={handleVolumeChange} 
         />
      </div>
    </div>
  )
};


export default App;
